#include <bits/stdc++.h>
using namespace std;

ostream &operator << (ostream &os, vector<int> v){
    os << "[";
    for(int i = 0; i < v.size(); i++){
        os << v[i];
        if(i != v.size() - 1){
            os << ", "; 
        }
    }
    os << "]";
    return os;
}


bool containsVector(vector<int> base, vector<int> sub){
    if(sub.size() == 0) return true;
    for(int i = 0; i < base.size() - sub.size() +1; i++){
        if( base[i] != sub[0] ) continue;
        bool exists = true;
        for(int j = 1; j < sub.size(); j++){
            if(base[i+j] != sub[j]) exists = false;
        }
        if(exists) return true;
    }
    return false;
}


int countLargestCommonArray(vector<int> nums1, vector<int> nums2){
    int maxCount = 0;
    for(int i = 0; i < nums1.size(); i++){
        vector<int> sub;
        int j = 0;
        while(containsVector(nums2, sub)){
            sub.push_back( nums1[i + j] );
            j++;
        }
        if(!containsVector(nums2, sub)){
            sub.pop_back();
        }
        if(sub.size() > maxCount){
            maxCount = sub.size();
            cout << nums1[i] << "  " << sub << endl;
        }
        if(sub.size() == nums1.size() || sub.size() == nums2.size()) return maxCount;
    }
    return maxCount;
}

